// SOAL 1
/////////
function sumNumbers(mixedArray) {
    return mixedArray
        .filter(item => typeof item === 'number')
        .reduce((sum, num) => sum + num, 0);
}

// Example
const mixedArray = ["3", 1, "string", null, false, undefined, 2];
console.log(sumNumbers(mixedArray)); 


//////////
// SOAL 2
/////////
function insertIntegers(integers, maxSize) {
    return integers.slice(0, maxSize);
}

// Example
const integers = [5, 10, 24, 3, 6, 7, 8];
const maxSize = 5;
console.log(insertIntegers(integers, maxSize)); 


//////////
// SOAL 3
/////////
function combineArrays(arr1, arr2) {
    return [...arr1, ...arr2];
}

// Example
const arr1 = [1, 2, 3];
const arr2 = [4, 5, 6];
console.log(combineArrays(arr1, arr2));


//////////
// SOAL 4
/////////
function findDuplicates(arr) {
    return arr.filter((item, index) => arr.indexOf(item) !== index);
}

// Example
const arr = [1, 2, 2, 3, 3, 4, 5];
console.log(findDuplicates(arr)); 


//////////
// SOAL 5
/////////
function arrayDifference(arr1, arr2) {
    return arr1.filter(item => !arr2.includes(item));
}

// Example
const arr1 = [1, 2, 3, 4, 5];
const arr2 = [3, 4, 5, 6, 7];
console.log(arrayDifference(arr1, arr2)); 
console.log(arrayDifference(arr2, arr1)); 
